        })

    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log('state from CREATEPRODUCT',this.state);
        this.props.createProductAction(this.state)
        this.props.history.push('/');   
    }



     onChangeInput=(value,e)=> {
        this.state.Category=value
